const {
    LOGGER
} = require('mango-logger');

const axios = require('axios');

class Message {
    async send(token, phoneNumbers, body, LineNumber) {
        const result = await axios.post('http://RestfulSms.com/api/MessageSend', {
            Messages: body,
            MobileNumbers: phoneNumbers,
            SendDateTime: null,
            CanContinueInCaseOfError: true,
            LineNumber,
        }, {
            headers: {
                'x-sms-ir-secure-token': token
            }
        }).then(e => {
            LOGGER.objToFile('sms-ir', 'Message', e.data);
            return e.data;
        }).catch(e => {
            LOGGER.errorToFile('sms-ir', e);
            throw new Error(e)
        })
        return result;
    }
}

module.exports = Message;