const {
    V2
} = require('../../index')
const token = new V2.Token();
const verificationCode = new V2.VerificationCode();
const msg = new V2.Message();
const APIKEY = '';
const SECRET = '';
const PHONENUMBER = '';
const LINENUMBER = ''

async function tokenTest() {
    const result = await token.get(APIKEY, SECRET)
    console.log(result)
}
async function verificationTest() {
    const tkn = await token.get(APIKEY, SECRET)
    const result = await verificationCode.send(tkn.TokenKey, PHONENUMBER, 12345)
    console.log(result)
}
async function verificationTemplateTest() {
    const tkn = await token.get(APIKEY, SECRET)
    const result = await verificationCode.sendTemplate(tkn.TokenKey, PHONENUMBER, 12345, 1)
    console.log(result)
}
async function messageTest() {
    const tkn = await token.get(APIKEY, SECRET)
    const result = await msg.send(tkn.TokenKey, [PHONENUMBER], ['message'], LINENUMBER)
    console.log(result)
}


